<?phperror_reporting(0);require_once './closed/session.php';require_once './closed/user.php';require_once './closed/module.php';require_once './closed/data.php';$moduleid = $_POST['moduleid'];$s = new Session();$u = new user();$m = new module();$d = new data();$session = $s->getCurrentSession();if(!$session||($session['sessiongroupid']!=2)){    echo json_encode(array('result'=>'redirection'));    require_once 'logout.php';    exit(0);}else {    $user = $u->getUserById($session['sessionuserid']);    $module = $m->getModuleById($moduleid);    $modulename = $module['modulename'];    $data = $d->getDataListByModule($moduleid);    echo json_encode(array('username' => $user['username'], 'photo' => $user['photo'], 'modulename' => $modulename, 'data' => $data));}?>